mapper.update:
update setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, status = #{status}, description = #{description}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}
